function dataOutput(sql) //１行ずつ処理する
//ＣＯＰＹ文終了コード出力
{
	var line;
	{
		if (!("fp_out" in global)) fp_out = undefined;
		if (!("dbh" in global)) dbh = undefined;
	}
	var rtn = pg_query(dbh, sql);

	while (line = pg_fetch_assoc(rtn)) //タブが必要かどうか(false:不要、true:必要)
	//１カラムずつ処理する
	//改行コード出力
	{
		var tabFlg = false;

		for (var key in line) //マスターデータ出力
		{
			var value = line[key];

			if (value == "") {
				var value = "\\N";
			}

			if (tabFlg == false) //２カラム目以降はタブ必要
				{
					tabFlg = true;
				} else {
				fwrite(fp_out, "\t");
			}

			fwrite(fp_out, value);
		}

		fwrite(fp_out, "\n");
	}

	fwrite(fp_out, "\\.\n");
};